mapper.update:
update data_dictionary

	 typeCode = #{typeCode},
	 typeName = #{typeName},
	 valueId = #{valueId},
	 valueName = #{valueName}

where id = #{id}

update data_dictionary

	 typeCode = #{typeCode},
	 typeName = #{typeName},
	 valueId = #{valueId},
	 valueName = #{valueName}

where typeCode = #{typeCode}

mapper.delete:
delete from data_dictionary

	and typeCode = #{typeCode}
	and typeName = #{typeName}
	and valueId = #{valueId}
	and valueName = #{valueName}
